mapper.select:
SELECT k.id id, title, k.grade_no grade_no , k.subject_no subject_no, order_no, k.status status, d.value grade , dc.value subject FROM cms_knowledge_point k LEFT JOIN cms_dictionary d on d.id=k.grade_no LEFT JOIN cms_dictionary dc on dc.id=k.subject_no WHERE 1=1
AND (title like '%${CmsKnowledge.title}%')
AND grade_no=#{CmsKnowledge.gradeNo ,jdbcType=INTEGER}
AND subject_no=#{CmsKnowledge.subjectNo, jdbcType=INTEGER}

SELECT *
FROM cms_knowledge_point
WHERE 1=1

	AND (title like '%${CmsKnowledge.title}%')


	AND grade_no=#{CmsKnowledge.gradeNo ,jdbcType=INTEGER}


	AND subject_no=#{CmsKnowledge.subjectNo, jdbcType=INTEGER}

mapper.update:
UPDATE cms_knowledge_point
SET 
status=#{CmsKnowledge.status}
WHERE
id=#{CmsKnowledge.id }

mapper.select:
SELECT *
FROM cms_knowledge_point
WHERE 1=1

	AND title=#{CmsKnowledge.title, jdbcType=VARCHAR}


	AND grade_no=#{CmsKnowledge.gradeNo ,jdbcType=INTEGER}


	AND subject_no=#{CmsKnowledge.subjectNo, jdbcType=INTEGER}
