mapper.insert:

  SELECT LAST_INSERT_ID()

insert into csm_dict (id, name, parent_id, 
  type, status, remark
  )
values (#{id,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR}, #{parent_id,jdbcType=INTEGER}, 
  #{type,jdbcType=VARCHAR}, #{status,jdbcType=INTEGER}, #{remark,jdbcType=VARCHAR}
  )

mapper.select:
select * from csm_dict where 1 = 1

	and (name like '%${dict.name}%')


	and parent_id=#{ dict.parentId }


	and type=#{ dict.type }


	and type=#{ dict.status }
